package JavaSessions;

public class MAthematicalConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i = 9/2;  //4 
		
		System.out.println(i);
		
		System.out.println(11/2);  //depend what kind of data type we write  5 
		
		System.out.println(11.0/2);  // now double /2 will be 5.5
		System.out.println(11/2.0);  // same result 5.5
		
		float d=9/2 ; // 4.0
		
		System.out.println(d);
		float df=9/2f; // 4.5
	
		System.out.println(df);
		
		System.out.println(0/9);  //0 
		//System.out.println(9/0); // ArithmeticException
		
		//System.out.println(0/0);  // ArithmeticException
		
		
		System.out.print("Hello"+"\n");   // \n -new line 
		//System.out.print("Selenium"+"\t" +"naveen");   // tab space will be generated 
		
		
		double a =5 ;  
		
		double b=2; 
		
		double c=a/b;  // take whole part not remiander 
		
		System.out.println(c); //2.5
		
		
		
	}
	

}
