package javaPractise;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int a =5; 
		
		if (a%2==0) {
			
			
			System.out.println("Your number is even");
		}

		else  {
			
			
			System.out.println("Your number is odd");
		}

	}

}
